export const getData = ({products, setProducts}) =>{
    const url = "http://localhost:18080/api/v1/products/"

    setProducts({...products, loading: true})

    fetch(url)
    .then(res => res.json())
    .then((respons) => {
        setProducts({ data: respons, loading: false })
        console.log(respons);
    })
    .catch((error) =>{ error &&
    setProducts({ error: "Error"});
    });
    
}